// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";

// We will add the Interfaces here

interface IFakeNFTMarketplace {
    // returns the price of an NFT from the FakeNFTMarketplace
    function purchase(uint256 _tokenId) external payable;

    //Returns the price in Wei for an NFT
    function getPrice() external view returns (uint256);

    //Returns a boolean value - true if available, false if not
    function available(uint256 _tokenId) external view returns (bool);
}

interface ICryptoDevsNFT {
    // returns the number of NFTs owned by the given address
    function balanceOf(address owner) external view returns (uint256);

    // tokenOfOwnerByIndex returns a tokenID at given index for owner
    function tokenOfOwnerByIndex(address owner, uint256 index)
        external
        view
        returns (uint256);
}

contract CryptoDevsDAO is Ownable {
    // Create an enum named Vote containing possible options for a vote
    //YAY = 0, NAY = 1
    enum Vote {
        YAY,
        NAY
    }

    // Create a struct named Proposal containing all relevant information
    struct Proposal {
        // nftTokenId - the tokenID of the NFT to purchase from FakeNFTMarketplace if the proposal passes
        uint256 nftTokenId;
        // deadline - the UNIX timestamp until which this proposal is active. Proposal can be executed after the deadline has been exceeded.
        uint256 deadline;
        // yayVotes - number of yay votes for this proposal
        uint256 yayVotes;
        // nayVotes - number of nay votes for this proposal
        uint256 nayVotes;
        // executed - whether or not this proposal has been executed yet. Cannot be executed before the deadline has been exceeded.
        bool executed;
        // voters - a mapping of CryptoDevsNFT tokenIDs to booleans indicating whether that NFT has already been used to cast a vote or not
        mapping(uint256 => bool) voters;
    }

    // Create a mapping of Proposal ID to Proposal
    mapping(uint256 => Proposal) public proposals;

    // Number of proposals that have been created
    // initial value of numPropsal is default '0'
    uint256 public numProposals;

    //  since we will be calling functions of the contracts 1.FakeNFTMarketplace and 2.CryptoDevsNFT contract, let's initialize reference/variables for those contracts.
    IFakeNFTMarketplace nftMarketplace;
    ICryptoDevsNFT cryptoDevsNFT;

    // The payable allows this constructor to accept an ETH deposit when it is being deployed by deployer to fill the DAO RTH treasury.
    constructor(
        address _nftMarketplaceContractAddress,
        address _cryptoDevsNftContractAddress
    ) payable {
        nftMarketplace = IFakeNFTMarketplace(_nftMarketplaceContractAddress);
        cryptoDevsNFT = ICryptoDevsNFT(_cryptoDevsNftContractAddress);
    }

    // Create a modifier which only allows a function to be called by someone who owns at least 1 CryptoDevsNFT
    modifier nftHolderOnly() {
        require(
            cryptoDevsNFT.balanceOf(msg.sender) > 0,
            "Tou are not a DAO member!"
        );
        _;
    }

    // create a proposal - nft members only
    // _nftTokenId refers to the NFT you want buy from the "FakeNFTMarkerplace"
    //returns ID of newly created proposal
    function createProposal(uint256 _nftTokenId)
        external
        nftHolderOnly
        returns (uint256)
    {
        require(
            nftMarketplace.available(_nftTokenId),
            "Nft is not available for sale"
        );

        Proposal storage proposal = proposals[numProposals];
        proposal.nftTokenId = _nftTokenId;
        proposal.deadline = block.timestamp + 5 minutes;

        numProposals++;

        // returning ID of newly created proposal
        return numProposals - 1;
    }

    //modifier that only allows a function to be called
    modifier activeProposalOnly(uint256 propsalId) {
        require(
            proposals[propsalId].deadline > block.timestamp,
            "voting Dedline exceeded"
        );
        _;
    }
    //modifier which only allows a function to be called if the given proposals' deadline HAS been exceeded and if the proposal has not yet been executed
    modifier inactiveProposalOnly(uint256 proposalId) {
        require(
            proposals[proposalId].deadline <= block.timestamp,
            "prposal is still active!"
        );
        require(
            proposals[proposalId].executed == false,
            "proposal is already executed!!"
        );
        _;
    }

    // vote on proposal - nft members only on an active proposal only
    // proposalId - the index of the proposal to vote on in the proposals array
    // vote - the type of vote they want to cast (yay or nay)
    function voteOnProposal(uint256 proposalId, Vote vote)
        external
        nftHolderOnly
        activeProposalOnly(proposalId)
    {
        Proposal storage proposal = proposals[proposalId];
        uint256 voterNFTBalance = cryptoDevsNFT.balanceOf(msg.sender);
        uint256 numVotes = 0;

        // calculating how many nft owned by the voter that haven't already been used for voting on this proposal
        for (uint256 i = 0; i < voterNFTBalance; i++) {
            uint256 tokenId = cryptoDevsNFT.tokenOfOwnerByIndex(msg.sender, i);
            if (proposal.voters[tokenId] == false) {
                numVotes++;
                proposal.voters[tokenId] == true;
            }
        }
        require(numVotes > 1, "you have already voted");

        if (vote == Vote.YAY) {
            proposal.yayVotes += numVotes;
        } else {
            proposal.nayVotes += numVotes;
        }
    }

    // execute a proposal (means here purching nft from market place)
    function executeProposal(uint256 proposalId)
        external
        nftHolderOnly
        inactiveProposalOnly(proposalId)
    {
        Proposal storage proposal = proposals[proposalId];

        // Did proposal pass?
        if (proposal.yayVotes > proposal.nayVotes) {
            uint256 nftPrice = nftMarketplace.getPrice();
            require(
                address(this).balance >= nftPrice,
                "contract has not enough funds"
            );
            nftMarketplace.purchase{value: nftPrice}(proposal.nftTokenId);
        }
        proposal.executed == true;
    }

    //withdrawEther allows the contract owner (deployer) to withdraw the ETH from the contract
    function withdrawEther() external onlyOwner {
        uint256 amount = address(this).balance;
        require(amount > 0, "Nothing to withdraw; contract balance empty");
        payable(owner()).transfer(amount);
    }

    // The following two functions allow the contract to accept ETH deposits
    // directly from a wallet without calling a function
    receive() external payable {}

    fallback() external payable {}
}

